(function() {
  $(function() {
    $('.select2').select2({
      theme: 'bootstrap'
    });
    window.myColorPicker = $('input.color').colorPicker({
      renderCallback: function($elm, toggled) {
        $elm.val('#' + this.color.colors.HEX);
      }
    });
    $(".tags").tagsInput({
      width: "100%",
      defaultText: "",
      delimiter: [","]
    });
    return $(".simple-data-table").DataTable({
      lengthChange: false,
      language: {
        loadingRecords: "Cargando ...",
        emptyTable: "No se encontraron resultados",
        zeroRecords: "No se encontraron resultados",
        info: "Mostrando desde _START_ hasta _END_ de _TOTAL_ registros",
        infoEmpty: "Mostrando desde 0 hasta 0 de 0 registros",
        infoFiltered: "(filtrado de _MAX_ registros en total)",
        search: "Filtro rpido: ",
        paginate: {
          previous: "Anterior",
          next: "Siguiente"
        }
      }
    });
  });

  window.NestedFormEvents.prototype.insertFields = function(content, assoc, link) {
    var input, last, target;
    if ($(link).closest(".form-gallery").length > 0) {
      last = $(link).closest(".form-gallery").find(".fields:last");
      content = $(content);
      input = content.find("input[type=file]");
      input.on("change", function() {
        var reader;
        content.insertBefore(last);
        if (this.files && this.files[0]) {
          reader = new FileReader();
          reader.onload = function(e) {
            return content.find("img").attr("src", e.target.result);
          };
          return reader.readAsDataURL(this.files[0]);
        }
      });
      return input.trigger("click");
    } else {
      target = $(link).data('target');
      if (target) {
        return $(content).appendTo($(target));
      } else {
        return $(content).insertBefore(link);
      }
    }
  };

  $(document).on("nested:fieldAdded", function(event) {
    var field;
    field = event.field;
    return field.find(".select2").select2({
      theme: "bootstrap"
    });
  });

}).call(this);
(function() {
  $(document).ready(function() {
    var ACTIVE_CLASS, HIDE_CLASS, SHOW_CLASS;
    SHOW_CLASS = "show";
    HIDE_CLASS = "hide";
    ACTIVE_CLASS = "active";
    return $(".tabs").on("click", "li a", function(e) {
      var $tab, href;
      e.preventDefault();
      $tab = $(this);
      href = $tab.attr("href");
      $(".active").removeClass(ACTIVE_CLASS);
      $tab.addClass(ACTIVE_CLASS);
      $(".show").removeClass(SHOW_CLASS).addClass(HIDE_CLASS).hide();
      return $(href).removeClass(HIDE_CLASS).addClass(SHOW_CLASS).hide().fadeIn(550);
    });
  });

}).call(this);
