(function() {
  $(document).ready(function() {
    var footer_height;
    $('#calculator-form').validate({
      rules: {
        wage: {
          required: true
        },
        gender: {
          required: true
        }
      },
      errorPlacement: function(error, element) {
        return error.insertAfter(element.parents('.input-group'));
      },
      messages: {
        wage: {
          number: "Ingrese un nmero vlido"
        }
      }
    });
    this.mobileWeb = /Mobile|iP(hone|od|ad)|Android|BlackBerry|IEMobile|Kindle|NetFront|Silk-Accelerated|(hpw|web)OS|Fennec|Minimo|Opera M(obi|ini)|Blazer|Dolfin|Dolphin|Skyfire|Zune/i.test(navigator.userAgent);
    $("#lightSlider").lightSlider({
      item: 4,
      controls: false,
      responsive: [
        {
          breakpoint: 800,
          settings: {
            item: 3,
            slideMove: 1,
            slideMargin: 6
          }
        }, {
          breakpoint: 480,
          settings: {
            item: 2,
            slideMove: 1
          }
        }
      ]
    });
    $('a#supuestos').on('click', function(e) {
      e.stopPropagation();
      e.preventDefault();
      $('#htmlModal').on('show.bs.modal', function() {
        var modalBody, modalDialog;
        modalBody = $(this).find('.modal-body');
        modalDialog = $(this).find('.modal-dialog');
      });
      return $('#htmlModal').modal();
    });
    $('a.play-video').on('click', function(e) {
      var iFrameCode, queryString, queryVars, vidHeight, vidWidth;
      queryString = $(this).attr('href').slice($(this).attr('href').indexOf('?') + 1);
      queryVars = $.parseQuery(queryString);
      if ('v' in queryVars) {
        e.preventDefault();
        if ($(this).attr('data-width')) {
          vidWidth = parseInt($(this).attr('data-width'));
        }
        if ($(this).attr('data-height')) {
          vidHeight = parseInt($(this).attr('data-height'));
        }
        iFrameCode = '<iframe width="100%" height="100%" scrolling="no" allowtransparency="true" allowfullscreen="true" src="http://www.youtube.com/embed/' + queryVars['v'] + '?rel=0&wmode=transparent&showinfo=0" frameborder="0"></iframe>';
        $('#mediaModal .modal-body').html(iFrameCode);
        $('#mediaModal .modal-title').html($(this).attr('title'));
        $('#mediaModal').on('show.bs.modal', function() {
          var modalBody, modalDialog, newModalWidth;
          modalBody = $(this).find('.modal-body');
          modalDialog = $(this).find('.modal-dialog');
          newModalWidth = vidWidth + parseInt(modalBody.css('padding-left')) + parseInt(modalBody.css('padding-right'));
          newModalWidth += parseInt(modalDialog.css('padding-left')) + parseInt(modalDialog.css('padding-right'));
          newModalWidth += 'px';
          $(this).find('.modal-dialog').css('width', newModalWidth);
        });
        $('#mediaModal').modal();
      }
    });
    $('#mediaModal').on('hidden.bs.modal', function() {
      $('#mediaModal .modal-body').html('');
    });
    $('.autoSb select').change(function() {
      return $(this).closest('form').submit();
    });
    if ($('.timeline-pagination').size() > 0) {
      $('.timeline-pagination a').addClass('hide');
      footer_height = $('#subscribe').height() + $('footer').height() - 20;
      $(window).on('scroll', function() {
        var more_posts_url;
        more_posts_url = $('.timeline-pagination a').attr('href');
        if (more_posts_url && $(window).scrollTop() > $(document).height() - $(window).height() - footer_height) {
          $('.timeline-pagination').html('<button class="btn btn-lg btn-info"><span class="glyphicon glyphicon-refresh glyphicon-refresh-animate"></span>Cargando...</button>');
          $.getScript(more_posts_url);
        }
      });
    }
  });

}).call(this);
