(function() {
  var set_select2;

  set_select2 = function() {
    $('.select2').select2({
      theme: 'bootstrap',
      language: 'es'
    });
    $('.load_specialties').off('select2:select');
    $('.load_specialties').on('select2:select', function(evt) {
      var gra_code, parent, target;
      gra_code = $(this).val();
      parent = $(this).closest('div.fields');
      target = $('.gra_specialties', parent);
      $.get('/resumes/specialties.json', {
        gra_code: gra_code
      }, function(data) {
        target.html('');
        $.each(data, function(index, value) {
          return target.append('<option value="' + value.id + '" ' + (index === 0 ? 'selected=selected' : '') + '>' + value.text + '</option>');
        });
        return target.trigger('change.select2');
      });
    });
  };

  $(function() {
    var ACTIVE_CLASS, HIDE_CLASS, SHOW_CLASS, form, options;
    jQuery.extend(jQuery.validator.messages, {
      maxlength: jQuery.validator.format(' Por favor ingresa no ms de {0} caracteres.')
    });
    $('[data-toggle="tooltip"]').tooltip();
    $(document).on('click', '.btnNext', function() {
      return $('.nav-tabs > .active').next('li').find('a').trigger('click');
    });
    SHOW_CLASS = "show";
    HIDE_CLASS = "hide";
    ACTIVE_CLASS = "active";
    $(".nav-tabs").on("click", "li a", function(e) {
      var $tab, href;
      e.preventDefault();
      $tab = $(this);
      href = $tab.attr("href");
      $(".active").removeClass(ACTIVE_CLASS);
      $tab.addClass(ACTIVE_CLASS);
      $(".show").removeClass(SHOW_CLASS).addClass(HIDE_CLASS).hide();
      return $(href).removeClass(HIDE_CLASS).addClass(SHOW_CLASS).hide().fadeIn(550);
    });
    form = $('form#resume-form').show();
    $('#user_birthday').datepicker;
    $("#user_phone").mask("9999-9999");
    $("#user_alt_phone").mask("9999-9999");
    $("#user_tax_id").mask("9999-999999-999-9");
    $("select#user_document_type").on("change", function() {
      if ($(this).find(":selected").val() === "dui") {
        alert("something");
        return $("#user_document_number").mask("9999999-9");
      } else {
        return $("#user_document_number").unmask();
      }
    });
    form.steps({
      headerTag: "h3",
      bodyTag: "section",
      transitionEffect: "fade",
      autoFocus: true,
      labels: {
        previous: "Anterior",
        next: "Siguiente",
        finish: "Finalizar"
      },
      setStep: 5,
      onStepChanging: function(event, currentIndex, newIndex) {
        if (currentIndex > newIndex) {
          return true;
        }
        if (currentIndex < newIndex) {
          form.find('.body:eq(' + newIndex + ') label.error').remove();
          form.find('.body:eq(' + newIndex + ') .error').removeClass('error');
        }
        form.validate().settings.ignore = ':disabled,:hidden';
        return form.valid();
      },
      onStepChanged: function(event, currentIndex, priorIndex) {
        if (currentIndex === 2 && priorIndex === 3) {
          form.steps('previous');
        }
      },
      onFinishing: function(event, currentIndex) {
        form.validate().settings.ignore = ':disabled,:hidden';
        return form.valid();
      },
      onFinished: function(event, currentIndex) {
        form.submit();
      }
    }).validate({
      errorPlacement: function(error, element) {
        element.before(error);
        console.log(error);
      },
      rules: {
        confirm: {
          equalTo: '#user_password'
        }
      }
    });
    options = {};
    options.ui = {
      container: "#pwd-container",
      showErrors: true,
      showProgressBar: true,
      showVerdictsInsideProgressBar: true,
      viewports: {
        progress: ".pwstrength_viewport_progress",
        errors: ".pwstrength_viewport_errors"
      }
    };
    $('#user_password').pwstrength(options);
    set_select2();
    $(document).on("nested:fieldAdded", function(event) {
      return set_select2();
    });
    $(document).on('change', '.btn-file :file', function() {
      var input, label, numFiles;
      input = $(this);
      numFiles = input.get(0).files ? input.get(0).files.length : 1;
      label = input.val().replace(/\\/g, '/').replace(/.*\//, '');
      input.trigger('fileselect', [numFiles, label]);
    });
    return $('.btn-file :file').on('fileselect', function(event, numFiles, label) {
      var input, log;
      input = $(this).parents('.input-group').find(':text');
      log = numFiles > 1 ? numFiles + ' files selected' : label;
      if (input.length) {
        input.val(log);
      } else {
        if (log) {
          alert(log);
        }
      }
    });
  });

}).call(this);
$(function(){
  $('button', '.item').on('click', function(e){
    e.preventDefault();
    e.stopPropagation();
    $(this).prev('input').click();
    $('.item').removeClass('active');
    $(this).closest('.item').addClass('active');
  });
});
